import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchUserData, updateUser } from '../auth/AuthSlice';
import EditProfile from './EditProfile';

const Profile = () => {
  const user = useSelector((state) => state.auth.user);
  const dispatch = useDispatch();
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    dispatch(fetchUserData());
  }, [dispatch]);

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleFormClose = () => {
    setIsEditing(false);
  };

  const handleUpdateUser = async (updatedUserData) => {
    await dispatch(updateUser({ Id: user.id, ...updatedUserData }));
    setIsEditing(false);
    // Optionally re-fetch user data
    dispatch(fetchUserData());
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <div className="container mt-6">
        <label>Search user</label>
        <input className="container mt-6 p-2" placeholder="name" />
      </div>
      <div className="container mt-6">
        <div className="row">
          <div className="col-md-12 mb-4 p-3">
            <div className="card">
              <div className="d-flex justify-content-between align-items-center">
                <h5 className="card-title">Profile Page</h5>
                <img src={user.userprofile} alt="User Profile" className="rounded-circle" width="50" />
                <button className="btn btn-primary" onClick={handleEditClick}>Edit</button>
              </div>
              <div className="card-body">
                <p className="card-text"><strong>Name:</strong> {user.name}</p>
                <p className="card-text"><strong>Email:</strong> {user.email}</p>
                <p className="card-text"><strong>Phone:</strong> {user.phone}</p>
                <p className="card-text"><strong>X (Twitter):</strong> {user.x}</p>
                <p className="card-text"><strong>Facebook:</strong> {user.facebook}</p>
                <p className="card-text"><strong>LinkedIn:</strong> {user.linkedin}</p>
                <p className="card-text"><strong>User Roles:</strong> {user.user_roles}</p>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className={`edit-profile-form ${isEditing ? 'show' : ''}`}>
        {isEditing && <EditProfileForm user={user} onClose={handleFormClose} onUpdateUser={handleUpdateUser} />}
      </div>
    </div>
  );
}

export default Profile;
