import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

// Async thunk to fetch priorities based on a date range
export const fetchPriorities = createAsyncThunk(
  'priority/fetchPriorities',
  async ({ start_date, end_date }, { rejectWithValue }) => {
    try {
      const response = await axios.get('http://localhost:8000/priority/getpriority', {
        params: { start_date, end_date }
      });
      return response.data;
    } catch (error) {
      return rejectWithValue(error.response.data);
    }
  }
);

const prioritySlice = createSlice({
  name: 'priority',
  initialState: {
    priorities: [], // to store all priorities from the fetched periods
    loading: false,
    error: null,
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchPriorities.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(fetchPriorities.fulfilled, (state, action) => {
        state.loading = false;
        state.priorities = action.payload.reduce((acc, period) => {
          return acc.concat(period.Priorities);
        }, []);
      })
      .addCase(fetchPriorities.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      });
  },
});

export default prioritySlice.reducer;
