import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { updateUser } from '../auth/AuthSlice';

const EditProfile = ({ user, onClose }) => {
  const [formData, setFormData] = useState({
    name: user.name,
    email: user.email,
    phone_number: user.phone_number,
    user_roles: user.user_roles,
    // user_photo: null,
  });

  const dispatch = useDispatch();

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'user_photo') {
      setFormData((prevData) => ({
        ...prevData,
        user_photo: files[0],
      }));
    } else {
      setFormData((prevData) => ({
        ...prevData,
        [name]: value,
      }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const updatedUserData = new FormData();
    updatedUserData.append('Id', user.id);
    updatedUserData.append('name', formData.name);
    updatedUserData.append('email', formData.email);
    updatedUserData.append('phone_number', formData.phone_number);
    updatedUserData.append('user_roles', formData.user_roles);
    // if (formData.user_photo) {
      // updatedUserData.append('user_photo', formData.user_photo);
      // console.log(updatedUserData.append('user_photo', formData.user_photo));
    // }
    dispatch(updateUser({Id: user.id,updatedUserData}));
    onClose();
  };

  return (
    <div className="edit-form">
      <form onSubmit={handleSubmit}>
        <h3>Edit Profile</h3>
        <div>
          <label>Name</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} />
        </div>
        <div>
          <label>Email</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} />
        </div>
        <div>
          <label>Phone</label>
          <input type="text" name="phone_number" value={formData.phone_number} onChange={handleChange} />
        </div>
        <div>
          <label>User Roles</label>
          <select name="user_roles" value={formData.user_roles} onChange={handleChange}>
            <option value="administration">Administration</option>
            <option value="growth champion">Growth Champion</option>
            <option value="decision maker">Decision Maker</option>
          </select>
        </div>
        <div>
          <label>Photo</label>
          <input type="file" name="user_photo" onChange={handleChange} />
        </div>
        <button type="submit">Save</button>
        <button type="button" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
};

export default EditProfile;
