import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

// Async thunk to create a new priority
export const createPriority = createAsyncThunk(
  'priority/createPriority',
  async (priorityData, { rejectWithValue }) => {
    try {
      const response = await axios.post('http://localhost:8000/api/priorities', priorityData);
      return response.data;
    } catch (error) {
      return rejectWithValue(error.response.data);
    }
  }
);

const prioritySlice = createSlice({
  name: 'priority',
  initialState: {
    priorities: [],
    loading: false,
    error: null,
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(createPriority.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(createPriority.fulfilled, (state, action) => {
        state.loading = false;
        state.priorities.push(action.payload.data);
      })
      .addCase(createPriority.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      });
  },
});

export default prioritySlice.reducer;
