import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchKPIs } from './KpiSlice'; // Ensure correct import path
import "./KpiListing.css";

const KPIList = () => {
  const dispatch = useDispatch();
  const kpis = useSelector((state) => state.kpi.kpi); 
  const status = useSelector((state) => state.kpi.status);
  const [isEdit, setIsEdit] = useState(false);
  // State to manage slider visibility
  const [isSliderOpen, setIsSliderOpen] = useState(false);
  const handleClick = () => {
    setIsEdit(true);
}

const handleFormClose = () => {
    setIsEdit(false);
}
  // Fetch KPIs when component mounts
  useEffect(() => {
    if (status === 'idle') {
      dispatch(fetchKPIs());
    }
  }, [status, dispatch]);

  // Function to toggle the slider
  const toggleSlider = () => {
    setIsSliderOpen(prev => !prev); // Toggle the state
  };

  return (
    <section className="kpi-section">
      <div className='d-flex'>
        <h2>Manage KPI</h2>
        <button className='ml-2' onClick={toggleSlider}>Add new KPI</button>
      </div>
      <div className="kpi-list">
        {kpis.map((kpi) => (
          <div key={kpi.id} className="kpi-card d-flex justify-content-between">
            <p>{kpi.name}</p>
            <p>{kpi.description}</p>
            <p>Unit: {kpi.unit}</p>
          </div>
        ))}
      </div>

      {/* Slider for creating a new KPI */}
      {/* <div className={`kpi-slider ${isSliderOpen ? 'open' : ''}`}>
        <div className="kpi-slider-content">
          <h3>Create New KPI</h3>
          <form>
            <div>
              <label htmlFor="name">Name</label>
              <input type="text" id="name" name="name" />
            </div>
            <div>
              <label htmlFor="description">Description</label>
              <input type="text" id="description" name="description" />
            </div>
            <div>
              <label htmlFor="unit">Unit</label>
              <input type="text" id="unit" name="unit" />
            </div>
            <button type="submit">Create KPI</button>
            <button type="button" onClick={toggleSlider}>Cancel</button>
          </form>
        </div>
      </div> */}
      <i className="bi bi-bell-fill fs-4" onClick={handleClick}></i>
                    <div className={`edit-profile-form ${isEdit ? 'show' : ''}`}>
                        {isEdit && <Cr onClose={handleFormClose} />}
                    </div>
    </section>
  );
};

export default KPIList;
