import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchKPIs } from './KpiSlice'; // Ensure correct import path
import "./KpiListing.css"
const KPIList = () => {
  const dispatch = useDispatch();
  
  // Correct the state key from kpi.kpi to kpi.kpis
  const kpis = useSelector((state) => state.kpi.kpi); 
  const status = useSelector((state) => state.kpi.status);
//   const error = useSelector((state) => state.kpi.error);

  // Use useEffect to handle side effects
  useEffect(() => {
    if (status === 'idle') {
      dispatch(fetchKPIs());
    }
  }, [status, dispatch]);

//   let content;

//   if (status === 'loading') {
//     content = <div>Loading KPIs...</div>;
//   } else if (status === 'succeeded') {
//     content = (
//       <div className="kpi-list">
//         {kpis.map((kpi) => (
//           <div key={kpi.id} className="kpi-card">
//             <h3>{kpi.name}</h3>
//             <p>{kpi.description}</p>
//             <p>Unit: {kpi.unit}</p>
//           </div>
//         ))}
//       </div>
//     );
//   } else if (status === 'failed') {
//     content = <div>{error}</div>;
//   }

  return (
    <section className="kpi-section">
      <h2>KPI List</h2>
      <div className="kpi-list">
        {kpis.map((kpi) => (
          <div key={kpi.id} className="kpi-card d-flex just">
            <p>{kpi.name}</p>
            <p>{kpi.description}</p>
            <p>Unit: {kpi.unit}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default KPIList;
