import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import "./CompanyListing.css";
import { fetchCompanyData, getCompanyDataById, setSelectedCompanyId } from '../../company/CompanySlice';
import EditCompanyInfo from './EditCompanyInfo';

const CompanyList = () => {
  const dispatch = useDispatch();
  const selectedCompanyId = useSelector((state) => state.company.selectedCompanyId);
  const selectedCompany = useSelector((state) => state.company.selectedCompanydata);
  const [isEdit, setIsEdit] = useState(false);

  useEffect(() => {
    dispatch(fetchCompanyData());
    if (selectedCompanyId) {
      dispatch(getCompanyDataById(selectedCompanyId));
    }
  }, [dispatch, selectedCompanyId]);

  const handleClick = () => {
    setIsEdit(true);
  };

  const handleFormClose = () => {
    setIsEdit(false);
  };


    // dispatch(getCompanyDataById(selectedCompanyId));

// console.log(selectedCompanyId,"iddddddddddd")
console.log(selectedCompany.company_name,"daaaaaaaaaaa")

  return (
    <section className="kpi-section">
      <h2>Manage KPI</h2>
      <div>
        <button className='mr-2' onClick={handleClick}>Update KPI</button>
        <div className={`edit-profile-form ${isEdit ? 'show' : ''}`}>
          {isEdit && <EditCompanyInfo onClose={handleFormClose} />}
        </div>
      </div>
      <div className="">
        {selectedCompany ? (
          <div key={selectedCompany.id} className="kpi-card">
            {/* <p>{selectedCompany.company_name}</p> */}
          </div>
        ) : (
          <p>No company selected</p>
        )}
      </div>
    </section>
  );
};

export default CompanyList;
