import React, { useState } from 'react';
import "./profile.css"
const EditProfile = ({ user, onClose }) => {
  const [formData, setFormData] = useState({ ...user });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission, e.g., dispatch an action to update user data
    onClose();
  };

  return (
    <div className="edit-profile-form">
      <div className="form-container">
        <h3>Edit Profile</h3>
        <button onClick={onClose} className="close-btn">X</button>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Name:</label>
            <input type="text" name="name" value={formData.name} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label>Email:</label>
            <input type="email" name="email" value={formData.email} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label>Phone:</label>
            <input type="text" name="phone" value={formData.phone_number} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label>X (Twitter):</label>
            <input type="text" name="x" value={formData.x} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label>Facebook:</label>
            <input type="text" name="facebook" value={formData.facebook} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label>LinkedIn:</label>
            <input type="text" name="linkedin" value={formData.linkedin} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label>User Roles:</label>
            <select name="user_roles" value={formData.user_roles} onChange={handleChange}>
              <option value="administration">Administration</option>
              <option value="growth champion">Growth Champion</option>
              <option value="decision maker">Decision Maker</option>
            </select>
          </div>
          <button type="submit" className="btn btn-primary">Save</button>
        </form>
      </div>
    </div>
  );
};

export default EditProfile;
