import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { createPriority } from './PrioritySlice';

export const AddPriority = () => {
    const dispatch = useDispatch();
    const { loading, error } = useSelector((state) => state.priorities.priority);
  
    const [priorityData, setPriorityData] = useState({
      priority_name: '',
      owner: '',
      start_value: 0,
      current_value: 0,
      target: 0,
      current_value_source: 'manual entry',
    });
  
    const handleChange = (e) => {
      setPriorityData({
        ...priorityData,
        [e.target.name]: e.target.value,
      });
    };
  
    const handleSubmit = (e) => {
      e.preventDefault();
      dispatch(createPriority(priorityData));
    };
  return (
    <div>
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="priority_name"
        placeholder="Priority Name"
        value={priorityData.priority_name}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="owner"
        placeholder="Owner"
        value={priorityData.owner}
        onChange={handleChange}
        required
      />
      <input
        type="number"
        name="start_value"
        placeholder="Start Value"
        value={priorityData.start_value}
        onChange={handleChange}
        required
      />
      <input
        type="number"
        name="current_value"
        placeholder="Current Value"
        value={priorityData.current_value}
        onChange={handleChange}
        required
      />
      <input
        type="number"
        name="target"
        placeholder="Target"
        value={priorityData.target}
        onChange={handleChange}
        required
      />
      <select
        name="current_value_source"
        value={priorityData.current_value_source}
        onChange={handleChange}
        required
      >
        <option value="manual entry">Manual Entry</option>
        <option value="connect a metric">Connect a Metric</option>
      </select>
      <button type="submit" disabled={loading}>
        {loading ? 'Submitting...' : 'Submit'}
      </button>
      {error && <p>{error.message}</p>}
    </form>
  </div>
  )
}
