import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchHelpAndFAQAData } from './HelpAndFAQSlice';
import './helpAndFaq.css'; // Import a CSS file for styling

export const HelpAndFAQ = () => {
  const dispatch = useDispatch();
  const helpfaq = useSelector((state) => state.helpfaq.helpfaq);
  const [selectedCategory, setSelectedCategory] = useState(null);

  useEffect(() => {
    dispatch(fetchHelpAndFAQAData());
  }, [dispatch]);

  // Log the helpfaq data to check its structure
  console.log('HelpFAQ Data:', helpfaq);

  const handleCardClick = (category) => {
    setSelectedCategory(category);
  };

  const handleBackClick = () => {
    setSelectedCategory(null);
  };

  return (
    <div>
      <h1>Help and FAQ</h1>
      {selectedCategory ? (
        <div className="selected-category">
          <h2>{selectedCategory.name}</h2>
          <ul>
            {/* Check if selectedCategory.questions is an array */}
            {selectedCategory.Questionsquestions && selectedCategory.Questions.questions.length > 0 ? (
              selectedCategory.questions.map((question, index) => (
                <li key={index}>{question}</li>
              ))
            ) : (
              <p>No questions available.</p>
            )}
          </ul>
          <button onClick={handleBackClick}>Back</button>
        </div>
      ) : (
        <div className="cards-container">
          {helpfaq.map((category, index) => (
            <div key={index} className="card" onClick={() => handleCardClick(category)}>
              <h2>{category.name}</h2>
              {/* Check if category.questions is an array */}
              <p>Number of Questions: {category.Questions && category.Questions.length}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};
