import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { createAnnouncement } from './AnnouncementSlice';
// import { selectCurrentUser } from './authSlice'; // Import the selector

import './announcement2.css';

export const Announcement = ({ onClose }) => {
    const dispatch = useDispatch();
    // const currentUser = useSelector(selectCurrentUser); // Get the current user from the store

    const [isCreateAnnouncementOpen, setIsCreateAnnouncementOpen] = useState(false);
    const [title, setTitle] = useState('');
    const [emailSubject, setEmailSubject] = useState('');
    const [message, setMessage] = useState('');
    const [isChecked, setIsChecked] = useState(false);

    const handleCreateAnnouncementClick = () => {
        setIsCreateAnnouncementOpen(true);
    };

    const handleCreateAnnouncementClose = () => {
        setIsCreateAnnouncementOpen(false);
    };

    const handleSave = () => {
        // Check if currentUser exists
        if (!currentUser) {
            console.error('User not logged in');
            return;
        }

        // Data to be sent to the server
        const announcementData = {
            title,
            emailSubject,
            message,
            isChecked,
            userId: currentUser.id, // Get userId from the current user
        };

        dispatch(createAnnouncement(announcementData));

        // Reset form fields
        setTitle('');
        setEmailSubject('');
        setMessage('');
        setIsChecked(false);

        // Close the secondary slider after saving
        handleCreateAnnouncementClose();
    };

    return (
        <div className="announcement-container">
            <button className="close-button" onClick={onClose}>Close</button>
            
            {/* Cart Section */}
            <div className="cart-section">
                <h2>Cart</h2>
                {/* Add your cart items here */}
                <p>No items in the cart</p>
            </div>
            
            {/* Buttons Section */}
            <div className="button-section">
                <button className="create-announcement-button" onClick={handleCreateAnnouncementClick}>
                    Create Announcement
                </button>
                <button className="cancel-button" onClick={onClose}>
                    Cancel
                </button>
            </div>

            {/* Secondary Slider for Create Announcement */}
            <div className={`create-announcement-slider ${isCreateAnnouncementOpen ? 'show' : ''}`}>
                <button className="close-button" onClick={handleCreateAnnouncementClose}>Close</button>
                <h2>Create New Announcement</h2>
                
                {/* Title Input */}
                <div className="input-section">
                    <label htmlFor="title">Announcement Title</label>
                    <input
                        type="text"
                        id="title"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                    />
                </div>

                {/* Email Subject Input */}
                <div className="input-section">
                    <label htmlFor="emailSubject">Email Subject</label>
                    <input
                        type="text"
                        id="emailSubject"
                        value={emailSubject}
                        onChange={(e) => setEmailSubject(e.target.value)}
                    />
                </div>

                {/* Message Input */}
                <div className="input-section">
                    <label htmlFor="message">Message</label>
                    <input
                        type="text"
                        id="message"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                    />
                </div>

                {/* Checkbox Input */}
                <div className="input-section">
                    <label>
                        <input
                            type="checkbox"
                            checked={isChecked}
                            onChange={(e) => setIsChecked(e.target.checked)}
                        />
                        Check me out
                    </label>
                </div>

                {/* Save and Cancel Buttons */}
                <div className="button-section">
                    <button className="save-button" onClick={handleSave}>Save</button>
                    <button className="cancel-button" onClick={handleCreateAnnouncementClose}>Cancel</button>
                </div>
            </div>
        </div>
    );
};
