import React, { useState } from 'react';

const EditProfile = ({ user, onClose, onUpdateUser }) => {
  const [formData, setFormData] = useState({
    name: user.name || '',
    email: user.email || '',
    phone: user.phone || '',
    user_roles: user.user_roles || '',
  });

  const [profilePicture, setProfilePicture] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleFileChange = (e) => {
    setProfilePicture(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Create FormData object to include both form fields and file
    const data = new FormData();
    data.append('name', formData.name);
    data.append('email', formData.email);
    data.append('phone', formData.phone);
    data.append('user_roles', formData.user_roles);
    if (profilePicture) {
      data.append('user_photo', profilePicture);
    }

    // Call the onUpdateUser function with FormData
    await onUpdateUser(data);

    // Call onClose function to close the edit form
    onClose();
  };

  return (
    <div className="edit-form">
      <form onSubmit={handleSubmit}>
        <h3>Edit Profile</h3>
        <div>
          <label>Name</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Phone</label>
          <input
            type="text"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>User Roles</label>
          <select
            name="user_roles"
            value={formData.user_roles}
            onChange={handleChange}
            required
          >
            <option value="">Select Role</option>
            <option value="administration">Administration</option>
            <option value="growth champion">Growth Champion</option>
            <option value="decision maker">Decision Maker</option>
          </select>
        </div>
        <div>
          <label>Profile Picture</label>
          <input
            type="file"
            accept="images/*"
            onChange={handleFileChange}
          />
        </div>
        <button type="submit">Save</button>
        <button type="button" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
};

export default EditProfile;
