// components/CreateHuddle.js
import React, { useState } from 'react';
import axios from 'axios';

const CreateHuddle = ({ huddleType }) => {
  const [formData, setFormData] = useState({
    name: '',
    owner: 'Subhadeep Subhadeep',
    videoConferenceLink: '',
    startTime: '',
    endTime: '',
    timeZone: '(UTC+05:30) Chennai, Kolkata, Mumbai, New Delhi',
    description: '',
    daysOfWeek: [],
    canMeetOnWeekends: false,
    tags: [],
    huddleType,
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === 'checkbox') {
      setFormData({
        ...formData,
        [name]: checked,
      });
    } else if (name === 'daysOfWeek') {
      const updatedDaysOfWeek = checked
        ? [...formData.daysOfWeek, value]
        : formData.daysOfWeek.filter(day => day !== value);
      setFormData({
        ...formData,
        daysOfWeek: updatedDaysOfWeek,
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/huddles', {
        ...formData,
        daysOfWeek: JSON.stringify(formData.daysOfWeek),
        tags: JSON.stringify(formData.tags)
      });
      // handle success (e.g., show a message, redirect, etc.)
    } catch (error) {
      // handle error
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Huddle Name:
        <input type="text" name="name" value={formData.name} onChange={handleChange} required />
      </label>
      <label>
        Owner:
        <input type="text" name="owner" value={formData.owner} onChange={handleChange} required readOnly />
      </label>
      <label>
        Video Conference Link:
        <input type="url" name="videoConferenceLink" value={formData.videoConferenceLink} onChange={handleChange} />
      </label>
      <label>
        Start Time:
        <input type="time" name="startTime" value={formData.startTime} onChange={handleChange} required />
      </label>
      <label>
        End Time:
        <input type="time" name="endTime" value={formData.endTime} onChange={handleChange} required />
      </label>
      <label>
        Time Zone:
        <select name="timeZone" value={formData.timeZone} onChange={handleChange} required>
          <option value="(UTC+05:30) Chennai, Kolkata, Mumbai, New Delhi">(UTC+05:30) Chennai, Kolkata, Mumbai, New Delhi</option>
          {/* Add other time zones as needed */}
        </select>
      </label>
      <label>
        Description:
        <textarea name="description" value={formData.description} onChange={handleChange}></textarea>
      </label>
      <fieldset>
        <legend>Check the days of the week the team meets:</legend>
        <label>
          <input type="checkbox" name="daysOfWeek" value="Monday" checked={formData.daysOfWeek.includes('Monday')} onChange={handleChange} />
          Monday
        </label>
        <label>
          <input type="checkbox" name="daysOfWeek" value="Tuesday" checked={formData.daysOfWeek.includes('Tuesday')} onChange={handleChange} />
          Tuesday
        </label>
        <label>
          <input type="checkbox" name="daysOfWeek" value="Wednesday" checked={formData.daysOfWeek.includes('Wednesday')} onChange={handleChange} />
          Wednesday
        </label>
        <label>
          <input type="checkbox" name="daysOfWeek" value="Thursday" checked={formData.daysOfWeek.includes('Thursday')} onChange={handleChange} />
          Thursday
        </label>
        <label>
          <input type="checkbox" name="daysOfWeek" value="Friday" checked={formData.daysOfWeek.includes('Friday')} onChange={handleChange} />
          Friday
        </label>
      </fieldset>
      <label>
        Can Meet On Weekends:
        <input type="checkbox" name="canMeetOnWeekends" checked={formData.canMeetOnWeekends} onChange={handleChange} />
      </label>
      <label>
        Add Tag:
        <input type="text" name="tags" value={formData.tags.join(', ')} onChange={e => setFormData({ ...formData, tags: e.target.value.split(', ') })} />
      </label>
      <button type="submit">Create Huddle</button>
    </form>
  );
};

export default CreateHuddle;
