import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

// Async thunk to create a new period
export const createPeriod = createAsyncThunk(
  'period/createPeriod', // Fixed the action name
  async (periodData, { rejectWithValue }) => {
    try {
      const response = await axios.post('http://localhost:8000/period/create', periodData); // Adjust the URL as needed
      return response.data;
    } catch (error) {
      return rejectWithValue(error.response.data);
    }
  }
);

const periodSlice = createSlice({
  name: 'period',
  initialState: {
    period: [],
    lastCreatedPeriodId:null,
    loading: false,
    error: null,
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(createPeriod.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(createPeriod.fulfilled, (state, action) => {
        state.loading = false;
        state.period.push(action.payload);
        state.lastCreatedPeriodId = action.payload.id; // Adjust based on response structure
      })
      .addCase(createPeriod.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      });
  },
});

export default periodSlice.reducer;
