import React, { useState } from 'react';

const EditProfile = ({ user, onClose, onUpdateUser }) => {
  const [formData, setFormData] = useState({
    name: user.name,
    email: user.email,
    user_phone: user.user_phone,
    user_roles: user.user_roles,
    user_photo:user.user_photo
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };
  const handleFileChange = (e) => {
    setProfilePicture(e.target.files[0]);
  };
  const handleSubmit = async(e) => {
    e.preventDefault();
    const data = new FormData();
    data.append('name', formData.name);
    data.append('email', formData.email);
    data.append('user_phone', formData.user_phone);
    data.append('user_roles', formData.user_roles);
    if (profilePicture) {
      data.append('profilePicture', profilePicture);
    }

    // Call the onUpdateUser function with FormData
    await onUpdateUser(data);

    // Call onClose function to close the edit form
    onClose();
  };

  return (
    <div className="edit-form">
      <form onSubmit={handleSubmit}>
        <h3>Edit Profile</h3>
        <div>
          <label>Name</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} />
        </div>
        <div>
          <label>Email</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} />
        </div>
        <div>
          <label>user_phone</label>
          <input type="text" name="user_phone" value={formData.user_phone} onChange={handleChange} />
        </div>
        <div>
          <label>User Roles</label>
          <select name="user_roles" value={formData.user_roles} onChange={handleChange}>
            <option value="administration">Administration</option>
            <option value="growth champion">Growth Champion</option>
            <option value="decision maker">Decision Maker</option>
          </select>
        </div>
        <div>
          <label>user_photo</label>
          <input  type="file"
            accept="image/*"
            onChange={handleFileChange} multiple={true}/>
        </div>
        <button type="submit">Save</button>
        <button type="button" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
};

export default EditProfile;
