import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const PeriodNavigation = () => {
    const {currentPeriodId} = useParams()
  const [data, setData] = useState({
    currentPeriod: null,
    previousPeriod: null,
    nextPeriod: null
  });

  useEffect(() => {
    const fetchAdjacentPeriods = async () => {
      try {
        const response = await axios.get(`/http://localhost:8000/period/get/${currentPeriodId}/adjacent`);
        setData(response.data);
      } catch (error) {
        console.error('Error fetching periods:', error);
      }
    };

    fetchAdjacentPeriods();
  }, [currentPeriodId]);

  const formatDate = (date) => {
    const options = { year: 'numeric', month: 'numeric', day: 'numeric' };
    return new Date(date).toLocaleDateString(undefined, options);
  };

  return (
    <div className="period-navigation">
      <div className="period-info">
        {data.previousPeriod && (
          <span className="period-label">
            Previous Period {formatDate(data.previousPeriod.start_date)} — {formatDate(data.previousPeriod.end_date)}
          </span>
        )}
        {data.currentPeriod && (
          <span className="period-label">
            Current Period {formatDate(data.currentPeriod.start_date)} — {formatDate(data.currentPeriod.end_date)}
          </span>
        )}
        {data.nextPeriod && (
          <span className="period-label">
            Next Period {formatDate(data.nextPeriod.start_date)} — {formatDate(data.nextPeriod.end_date)}
          </span>
        )}
      </div>
    </div>
  );
};

export default PeriodNavigation;
