import React from 'react';
import { useParams } from 'react-router-dom';
import '.'; // Import the CSS file for styling

export const QuestionDetail = () => {
  const { id } = useParams();
  
  // Example placeholder text for now
  const questionDetails = {
    1: `Do you have a survey that you want to send out again? Maybe you're using the Alignment Company Survey that is in Draft status in your current account? Cloning a survey is easy!
    
    Cloning a CLOSED or COMPLETED survey
    
    Navigate to the survey results page by clicking the pie chart icon to the right of the survey list in the Manage Surveys page. At the top of the results, you'll see a button to "clone" the survey. Click that button for a fresh start with all of the same questions! Update your survey settings and press Save.
    
    Cloning a DRAFT survey
    
    Navigate to the survey page by clicking the "edit" link to the right of the survey list in the Manage Surveys page. At the bottom of the page, you'll see a button to "clone" the survey. Click that button for a fresh start with all of the same questions! Update your survey settings and press Save
    
    Please Note: Culture Tools (like surveys) are Admin Only features. If you do not see the Culture menu, ask an Admin at your company for help!
    
    Last Updated Thursday, January 4, 2024`,
    2: "Details for Question 2",
    // Add more question details
  };

  return (
    <div className="question-detail-container">
      <h1>Question Details</h1>
      <div className="question-detail">
        {questionDetails[id] ? (
          questionDetails[id].split('\n').map((line, index) => (
            <p key={index}>{line}</p>
          ))
        ) : (
          <p>Question not found</p>
        )}
      </div>
    </div>
  );
};
