import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './UpdateKpiPriority.css';

const UpdateKpiPriority = () => {
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [products, setProducts] = useState([]);
  const [isSliderVisible, setIsSliderVisible] = useState(false);
  
  const fetchProducts = async (page) => {
    try {
      const response = await axios.get(`http://localhost:8080/api/products?page=${page}&pageSize=1`);
      const { products, totalPages } = response.data;
      setProducts(products);
      setTotalPages(totalPages);
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  };

  useEffect(() => {
    fetchProducts(currentPage);
  }, [currentPage]);

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const toggleSlider = () => {
    setIsSliderVisible(!isSliderVisible);
  };

  return (
    <div>
      <button onClick={toggleSlider}>
        {isSliderVisible ? 'Hide' : 'Show'} Slider
      </button>
      <div className={`kpi-slider ${isSliderVisible ? 'show' : ''}`}>
        <div className="kpi-slider-content">
          <button className="close-button" onClick={toggleSlider}>Ã—</button>
          <h2>KPI Priority Slider</h2>
          {/* Add slider content here */}
        </div>
      </div>
      <div>
        {products.map((product) => (
          <div key={product.id}>
            {product.start_date} - {product.end_date}
          </div>
        ))}
      </div>
      <div>
        <button onClick={handlePrevPage} disabled={currentPage === 1}>
          Previous Page
        </button>
        <button onClick={handleNextPage} disabled={currentPage === totalPages}>
          Next Page
        </button>
      </div>
    </div>
  );
};

export default UpdateKpiPriority;
