import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { createTask } from './TaskSlice'; // Ensure this path is correct for your project structure

const TaskForm = () => {
  const [shortTaskName, setShortTaskName] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [recurring, setRecurring] = useState(false);
  const [assignedTo, setAssignedTo] = useState([]);
  const [priorityId, setPriorityId] = useState(null);
  const [huddleId, setHuddleId] = useState(null);
  const [visibility, setVisibility] = useState('Everyone');
  const [notes, setNotes] = useState('');
  const [prioritySearch, setPrioritySearch] = useState('');
  const [showDropdown, setShowDropdown] = useState(false);

  const dispatch = useDispatch();
  const searchResult = useSelector((state) => state.priority.searchResult);

  const handlePrioritySearch = (e) => {
    const searchInput = e.target.value;
    setPrioritySearch(searchInput);
    setShowDropdown(searchInput.length > 0);
  };

  const handlePrioritySelect = (priority) => {
    setPriorityId(priority.id);
    setPrioritySearch(priority.name);
    setShowDropdown(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const taskData = {
      shortTaskName,
      dueDate,
      recurring,
      assignedTo,
      priorityId,
      huddleId,
      visibility,
      notes,
    };
    dispatch(createTask(taskData));
    setShortTaskName('');
    setDueDate('');
    setRecurring(false);
    setAssignedTo([]);
    setPriorityId(null);
    setHuddleId(null);
    setVisibility('Everyone');
    setNotes('');
    setPrioritySearch('');
  };

  return (
    <div className='d-flex align-content-center justify-content-center'>
      <form onSubmit={handleSubmit} className='w-50'>
        {/* Other form fields */}
        <div className='form-group mb-3'>
          <label>Align to a Priority</label>
          <input
            type="text"
            className='form-control'
            value={prioritySearch}
            onChange={handlePrioritySearch}
            placeholder="Search for a priority..."
          />
          {showDropdown && (
            <ul className='dropdown-menu show'>
              {searchResult
                .filter(priority => priority.priority_name.toLowerCase().includes(prioritySearch.toLowerCase()))
                .map(priority => (
                  <li
                    key={priority.id}
                    className='dropdown-item'
                    onClick={() => handlePrioritySelect(priority)}
                  >
                    {priority.priority_name}
                  </li>
                ))
              }
            </ul>
          )}
        </div>
        {/* Other form fields */}
        <button type="submit" className='btn btn-primary'>Save Task</button>
      </form>
    </div>
  );
};

export default TaskForm;
