export const fetchUserData = createAsyncThunk("fetchUserData", async (_, { getState }) => {
  const state = getState();
  const token = state.auth.token; // Assuming you store the token in auth slice
  console.log(token);
  const res = await fetch(`http://localhost:8000/user/getlogin`, {
    headers: {
      "Authorization": `Bearer ${token}`,
    },
  });

  if (!res.ok) {
    throw new Error('Failed to fetch user data');
  }

  return res.json();
});

// update user 
export const updateUser = createAsyncThunk(
  "updateUser",
  async ({ Id, ...data }, { rejectWithValue }) => {
    try {
      const res = await fetch(`http://localhost:8000/user/updateuser/${Id}`, {
        method: "PUT",
        headers: {
          "Content-type": "application/json",
        },
        body: JSON.stringify(data),
