import React, { useState } from 'react';

const EditProfile = ({ user, onClose, onUpdateUser }) => {
  const [formData, setFormData] = useState({
    name: user.name,
    email: user.email,
    phone: user.phone,
    user_roles: user.user_roles,
  });

  const [image, setImage] = useState({ preview: '', data: '' });
  const [status, setStatus] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleFileChange = (e) => {
    const img = {
      preview: URL.createObjectURL(e.target.files[0]),
      data: e.target.files[0],
    };
    setImage(img);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    let formDataToSend = new FormData();
    formDataToSend.append('name', formData.name);
    formDataToSend.append('email', formData.email);
    formDataToSend.append('userphone', formData.phone);
    formDataToSend.append('user_roles', formData.user_roles);
    if (image.data) {
      formDataToSend.append('user_photo', image.data);
    }

    try {
      await onUpdateUser(formDataToSend);
      setStatus('Profile updated successfully');
      onClose();
    } catch (error) {
      setStatus(`Error updating profile: ${error}`);
    }
  };

  return (
    <div className="edit-form">
      <form onSubmit={handleSubmit}>
        <h3>Edit Profile</h3>
        <div>
          <label>Name</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} />
        </div>
        <div>
          <label>Email</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} />
        </div>
        <div>
          <label>Phone</label>
          <input type="text" name="phone" value={formData.phone} onChange={handleChange} />
        </div>
        <div>
          <label>User Roles</label>
          <select name="user_roles" value={formData.user_roles} onChange={handleChange}>
            <option value="administration">Administration</option>
            <option value="growth champion">Growth Champion</option>
            <option value="decision maker">Decision Maker</option>
          </select>
        </div>
        <div>
          <label>Profile Picture</label>
          <input type="file" name="user_photo" onChange={handleFileChange} />
          {image.preview && <img src={image.preview} alt="Preview" width="100" />}
        </div>
        <button type="submit">Save</button>
        <button type="button" onClick={onClose}>Cancel</button>
        {status && <p>{status}</p>}
      </form>
    </div>
  );
};

export default EditProfile;
