import React, { useState } from "react";

const EditProfile = ({ user, onClose, onUpdateUser }) => {
  const [userPhoto, setUserPhoto] = useState({ preview: '', data: '' });

  const handleFileChange = (e) => {
    const img = {
      preview: URL.createObjectURL(e.target.files[0]),
      data: e.target.files[0],
    };
    setUserPhoto(img);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    let formData = new FormData();
    formData.append('user_photo', userPhoto.data);
    onUpdateUser(user.id, formData); // Pass user ID and formData
    onClose();
  };

  return (
    <div className="edit-form">
      <form onSubmit={handleSubmit}>
        <h3>Edit Profile</h3>
        <div>
          <label>File</label>
          <input type='file' name='user_photo' onChange={handleFileChange} />
        </div>
        {userPhoto.preview && <img src={userPhoto.preview} alt="preview" width="100" />}
        <button type="submit">Save</button>
        <button type="button" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
};

export default EditProfile;
