import React, { useState } from "react";

const EditProfile = ({ user, onClose, onUpdateUser }) => {
  const [formData, setFormData] = useState({
    name: user.name,
    email: user.email,
    user_phone: user.user_phone,
    user_roles: user.user_roles,
  });
  const [userPhoto, setUserPhoto] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setUserPhoto(file);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formDataToSend = new FormData();
    formDataToSend.append('name', formData.name);
    formDataToSend.append('email', formData.email);
    formDataToSend.append('user_phone', formData.user_phone);
    formDataToSend.append('user_roles', formData.user_roles);
    if (userPhoto) {
      formDataToSend.append('user_photo', userPhoto);
    }
    console.log(formData);
    onUpdateUser(formDataToSend);
  };

  return (
    <div className="edit-form">
      <form onSubmit={handleSubmit}>
        <h3>Edit Profile</h3>
        <div>
          <label>Name</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} />
        </div>
        <div>
          <label>Email</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} />
        </div>
        <div>
          <label>Phone</label>
          <input name="user_phone" value={formData.user_phone} onChange={handleChange} />
        </div>
        <div>
          <label>User Roles</label>
          <select name="user_roles" value={formData.user_roles} onChange={handleChange}>
            <option value="administration">Administration</option>
            <option value="growth champion">Growth Champion</option>
            <option value="decision maker">Decision Maker</option>
          </select>
        </div>
        <div>
          <label>Photo</label>
          <input name="user_photo" type="file" accept="image/*" onChange={handleFileChange} />
        </div>
        <button type="submit">Save</button>
        <button type="button" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
};

export default EditProfile;
