import React,{ useState } from 'react'
import "./footer.css";

const Footer = () => {
    const [showForm, setShowForm] = useState(false);
    const toggleForm = ()=>{
        setShowForm(!showForm);
    }

    const closeForm =()=>{
        setShowForm(!showForm)
    }

  return (
    <div>
      <footer className="footer fixed-bottom bg-white">
        <div className="container text-end p-5">
          <span className="text-primary">
            <button type="button" className="btn btn-primary p-3" onClick={toggleForm}>
              Create a Company
            </button>
          </span>
        </div>
      </footer>

      <div className={`slide-in-form ${showForm ? 'show' : ''}`}>
        <form className="p-4 d-flex flex-column justify-content-between form-container">
          <div>
            <h3>Create a Company</h3>
            {[...Array(7)].map((_, i) => (
              <div className="mb-3" key={i}>
                <label htmlFor={`companyField${i}`} className="form-label">Field {i + 1}</label>
                <input type="text" className="form-control" id={`companyField${i}`} placeholder={`Enter field ${i + 1}`} />
              </div>
            ))}
          </div>
          <div className="d-flex justify-content-end mt-3">
            <button type="submit" className="btn btn-primary me-2">Submit</button>
            <button type="button" className="btn btn-secondary" onClick={closeForm}>Cancel</button>
          </div>
        </form>
      </div>
    </div>

  )
}

export default Footer
