import React, {useState} from "react"
const EditProfile = ({ user, onClose, onUpdateUser }) => {
  const [user_photo, setImage] = useState({ preview: '', data: '' });

  // const [formData, setFormData] = useState({
  //   name: user.name,
  //   // email: user.email,
  //   // user_phone: user.user_phone,
  //   // user_roles: user.user_roles,
  // });

  // const handleChange = (e) => {
  //   const { name, value } = e.target;
  //   setFormData((prevData) => ({
  //     ...prevData,
  //     [name]: value,
  //   }));
  // };
  const handleFileChange = (e) => {
    const img = {
      preview: URL.createObjectURL(e.target.files[0]),
      data: e.target.files[0],
    }
    setImage(img)
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    // e.preventDefault()
    let formData = new FormData()
    formData.append('file', user_photo.data)
    onUpdateUser(formData);
  };

  return (
    <div className="edit-form">
      <form onSubmit={handleSubmit}>
        <h3>Edit Profile</h3>
        {/* <div>
          <label>Name</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} />
        </div> */}
        <div>
          <label>file</label>
          <input type='file' name='user_photo' onChange={handleFileChange}></input>
        </div>
        {/* <div>
          <label>Email</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} />
        </div>
        <div>
          <label>Phone</label>
          <input  name="user_phone" value={formData.user_phone} onChange={handleChange} />
        </div> */}
        {/* <div>
          <label>User Roles</label>
          <select name="user_roles" value={formData.user_roles} onChange={handleChange}>
            <option value="administration">Administration</option>
            <option value="growth champion">Growth Champion</option>
            <option value="decision maker">Decision Maker</option>
          </select>
        </div> */}
        <button type="submit">Save</button>
        <button type="button" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
};

export default EditProfile;
