import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { updateUser } from '../auth/AuthSlice';

const EditProfileForm = ({ user, onClose }) => {
  const [formData, setFormData] = useState({
    name: user.name,
    email: user.email,
    phone: user.phone,
    x: user.x,
    facebook: user.facebook,
    linkedin: user.linkedin,
    user_roles: user.user_roles,
  });

  const dispatch = useDispatch();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(updateUser({ userId: user.id, ...formData }));
    onClose();
  };

  return (
    <div className="edit-form">
      <form onSubmit={handleSubmit}>
        <h3>Edit Profile</h3>
        <div>
          <label>Name</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} />
        </div>
        <div>
          <label>Email</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} />
        </div>
        <div>
          <label>Phone</label>
          <input type="text" name="phone" value={formData.phone} onChange={handleChange} />
        </div>
        <div>
          <label>X (Twitter)</label>
          <input type="text" name="x" value={formData.x} onChange={handleChange} />
        </div>
        <div>
          <label>Facebook</label>
          <input type="text" name="facebook" value={formData.facebook} onChange={handleChange} />
        </div>
        <div>
          <label>LinkedIn</label>
          <input type="text" name="linkedin" value={formData.linkedin} onChange={handleChange} />
        </div>
        <div>
          <label>User Roles</label>
          <select name="user_roles" value={formData.user_roles} onChange={handleChange}>
            <option value="administration">Administration</option>
            <option value="growth champion">Growth Champion</option>
            <option value="decision maker">Decision Maker</option>
          </select>
        </div>
        <button type="submit">Save</button>
        <button type="button" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
};

export default EditProfileForm;
