import React, { useState } from "react";

const EditProfile = ({ user, onClose, onUpdateUser }) => {
  const [file, setUserPhoto] = useState();

  // const handleFileChange = (e) => {
  //   // const img = {
  //   //   // preview: URL.createObjectURL(e.target.files[0]),
  //   //   data: e.target.files[0],
      
  //   // };
  //   console.log(e.target.files[0]);
  //   // console.log(img);
  //   setUserPhoto(e.target.files[0]);
  // };

  const handleSubmit = (e) => {
    e.preventDefault();
    let formData = new FormData();
    formData.append('user_photo', file);
    console.log(formData.append('user_photo', file));
    // Log the FormData contents
    for (let pair of formData.entries()) {
      console.log(pair[0] + ': ' + pair[1]);
    }
console.log(formData.entries);
    onUpdateUser(formData); // Pass user ID and formData
    // onClose();
  };

  return (
    <div className="edit-form">
      <form onSubmit={handleSubmit}>
        <h3>Edit Profile</h3>
        <div>
          <label>File</label>
          <input type='file' filename='file' accept="image/*" onChange={(e)=>setUserPhoto(e.target.files[0])} />
        </div>
        {/* {user_photo.preview && <img src={user_photo.preview} alt="preview" width="100" />} */}
        <button type="submit">Save</button>
        <button type="button" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
};

export default EditProfile;
