import React, { useState, useEffect } from 'react';
import "../../profile/searchProfile.css";
import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';
import { searchUsersByName } from '../../auth/AuthSlice';
import { fetchPriorities } from './PrioritySlice'; // Import the fetchPriorities action
import { Link } from 'react-router-dom';

const UpdateKpiPriority = () => {
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [products, setData] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedPeriod, setSelectedPeriod] = useState(null);
  
  const dispatch = useDispatch();
  const searchResults = useSelector((state) => state.auth.searchResults);
  const { priority, loading, error } = useSelector((state) => state.priority); // Access priority state from redux

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
    if (e.target.value) {
      dispatch(searchUsersByName(e.target.value));
    }
  };

  const fetchProducts = async (page) => {
    try {
      const response = await axios.get(`http://localhost:8000/period/period/?page=${page}&pageSize=1`);
      const { products, totalPages } = response.data;
      setData(products);
      setTotalPages(totalPages);
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  };

  useEffect(() => {
    fetchProducts(currentPage);
  }, [currentPage]);

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handlePeriodSelect = (product) => {
    setSelectedPeriod(product);
    dispatch(fetchPriorities({ start_date: product.start_date, end_date: product.end_date }));
  };

  return (
    <div>
      
    </div>
  );
};

export default UpdateKpiPriority;
