import React, { useState } from "react";

const EditProfile = ({ user, onClose, onUpdateUser }) => {
  const [name, setName] = useState(user.name);
  // const [file, setUserPhoto] = useState(null);

  const handleFileChange = (e) => {
    setUserPhoto(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    let formData = new FormData();
    formData.append('name', name); // Update name in FormData
    if (file) {
      formData.append('user_photo', file); // Append file if it exists
    }

    onUpdateUser(formData); // Pass user ID and formData
    onClose();
  };

  return (
    <div className="edit-form">
      <form onSubmit={handleSubmit}>
        <h3>Edit Profile</h3>
        <div>
          <label>Name</label>
          <input type='text' value={name} onChange={(e) => setName(e.target.value)} />
        </div>
        <div>
          <label>File</label>
          <input type='file' accept="image/*" onChange={handleFileChange} />
        </div>
        {/* You can add a preview of the selected image if needed */}
        {/* {file && <img src={URL.createObjectURL(file)} alt="preview" width="100" />} */}
        <button type="submit">Save</button>
        <button type="button" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
};

export default EditProfile;
