import React, { useState } from "react";

const EditProfile = ({ user, onClose, onUpdateUser }) => {
  const [file, setFile] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (file) {
      let formData = new FormData();
      formData.append('user_photo', file);
      onUpdateUser(formData);
    } else {
      // Handle case where no file is selected (optional)
      console.log('No file selected');
    }
  };

  return (
    <div className="edit-form">
      <form onSubmit={handleSubmit}>
        <h3>Edit Profile</h3>
        <div>
          <label>Profile Photo</label>
          <input type='file' accept="image/*" onChange={handleFileChange} />
        </div>
        <div>
          <label>Profile Photo</label>
          <input type='file'  onChange={handleFileChange} />
        </div>
        {/* You can add a preview of the selected image if needed */}
        {/* {file && <img src={URL.createObjectURL(file)} alt="preview" width="100" />} */}
        <button type="submit">Save</button>
        <button type="button" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
};

export default EditProfile;
