import React, { useState } from 'react';

const EditProfile = ({ user, onClose, onUpdateUser }) => {
  const [formData, setFormData] = useState({
    name: user.name,
    email: user.email,
    phone: user.phone,
    user_roles: user.user_roles,
    user_photo: user.user_photo, // Add profile picture to state
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleFileChange = (e) => {
    setFormData((prevData) => ({
      ...prevData,
      user_photo: e.target.files[0], // Add file to state
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formDataToSend = new FormData();
    Object.entries(formData).forEach(([key, value]) => {
      formDataToSend.append(key, value);
    });

    onUpdateUser(formDataToSend); // Pass FormData to update function
  };

  return (
    <div className="edit-form">
      <form onSubmit={handleSubmit}>
        <h3>Edit Profile</h3>
        <div>
          <label>Name</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} />
        </div>
        <div>
          <label>Email</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} />
        </div>
        <div>
          <label>Phone</label>
          <input type="text" name="phone" value={formData.phone} onChange={handleChange} />
        </div>
        <div>
          <label>User Roles</label>
          <select name="user_roles" value={formData.user_roles} onChange={handleChange}>
            <option value="administration">Administration</option>
            <option value="growth champion">Growth Champion</option>
            <option value="decision maker">Decision Maker</option>
          </select>
        </div>
        <div>
          <label>Profile Picture</label>
          <input type="file" name="user_photo" onChange={handleFileChange} />
        </div>
        <button type="submit">Save</button>
        <button type="button" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
};

export default EditProfile;
