import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PeriodNavigation = ({ currentPeriodId }) => {
  const [data, setData] = useState({
    currentPeriod: null,
    previousPeriod: null,
    nextPeriod: null
  });

  const fetchAdjacentPeriods = async () => {
    try {
      const response = await axios.get(`/http://localhost:8000/period/${currentPeriodId}/adjacent`);
      setData(response.data);
    } catch (error) {
      console.error('Error fetching periods:', error);
    }
  };

  useEffect(() => {
    fetchAdjacentPeriods();
  }, [currentPeriodId]);

  return (
    <div>
      <button onClick={() => fetchAdjacentPeriods('previous')}>Previous Period</button>
      <button onClick={() => fetchAdjacentPeriods('next')}>Next Period</button>
      <div>
        <h2>Current Period</h2>
        {data.currentPeriod && (
          <>
            <p>Start Date: {data.currentPeriod.start_date}</p>
            <p>End Date: {data.currentPeriod.end_date}</p>
          </>
        )}
        <h2>Previous Period</h2>
        {data.previousPeriod && (
          <>
            <p>Start Date: {data.previousPeriod.start_date}</p>
            <p>End Date: {data.previousPeriod.end_date}</p>
          </>
        )}
        <h2>Next Period</h2>
        {data.nextPeriod && (
          <>
            <p>Start Date: {data.nextPeriod.start_date}</p>
            <p>End Date: {data.nextPeriod.end_date}</p>
          </>
        )}
      </div>
    </div>
  );
};

export default PeriodNavigation;
